import Header from '../Header';
import style from './ShopPage.module.css';
// import brown from '../images/brown.ppg';
// import green from '../images/green.ppg';
// import green__big from '../images/green__big.ppg';
// import yellow from '../images/yellow.ppg';
import { useState } from 'react';

const products = [
    { id: 1, title: 'Latte', price: 7.45 },
    { id: 2, title: 'Espresso', price: 3.45 },
    { id: 3, title: 'Macchiato', price: 6.45 },
    { id: 4, title: 'Cappucino', price: 7.45 },
];

const ShopPage = () => {
    const [cart, setCart] = useState([]);

    const addToCart = (product) => {
        setCart([...cart, product]);
    };

    const total = cart.reduce((sum, item) => sum + item.price, 0);

    return (
        <div className={style.shop}>
            <h1>Coffee shop</h1>
            <h2>Types of coffee</h2>
            <ul>
                {products.map((p) => (
                    <li key={p.id}>
                        {p.title} & {p.price}$
                        <button onClick={() => addToCart(p)}>Pay</button>
                    </li>
                ))}
            </ul>

            <h2>Market</h2>
            {cart.length === 0 ? (
                <p>Empty</p>
            ) : (
                <>
                    <ul>
                        {cart.map((item, i) => (
                            <li key={i}>
                                {item.title} & {item.price}$
                            </li>
                        ))}
                    </ul>
                    <strong>All what you choose: {total}$ </strong>
                </>
            )}
        </div>
    );
};

export default ShopPage;
