import './App.css';
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import Contacts from './Contacts/Contacts';
import Content from './Content/Content';
import Cups from './Cups/Cups';
import Delicious from './Delicious/Delicious';
import Events from './Events/Events';
import Footer from './Footer/Footer';
import Header from './Header/Header';
import ShopPage from './Header/ShopPage/ShopPage';
import Welcome from './Welcome/Welcome';

function MainLayout() {
  const location = useLocation();

  // Показывать Header и Footer только если не на странице /shop
  const showHeaderFooter = location.pathname !== '/shop';

  return (
    <>
      {showHeaderFooter && <Header />}
      <Routes>
        <Route path="/" element={<Welcome />} />
        <Route path="/shop" element={<ShopPage />} />
        {/* Другие маршруты */}
        <Route path="/contacts" element={<Contacts />} />
        <Route path="/content" element={<Content />} />
        <Route path="/cups" element={<Cups />} />
        <Route path="/delicious" element={<Delicious />} />
        <Route path="/events" element={<Events />} />
      </Routes>
      {showHeaderFooter && <Footer />}
    </>
  );
}

function App() {
  return (
    <BrowserRouter>
      <div className="container">
        <MainLayout />
      </div>
    </BrowserRouter>
  );
}

export default App;
